import React, { useState, useContext } from 'react';
import { Container, Row, Col, Form, FormGroup, Button } from 'reactstrap';
import { Link } from 'react-router-dom';

import "../styles/register.css";
import registerImg from "../assets/images/register.png";
import userIcon from "../assets/images/user.png";
import {AuthContext} from "./../context/AuthContext"
import{BASE_URl} from "./../utils/config"

const Register = () => {
    const [credentials, setCredentials] = useState({
      username:undefined,
      email: undefined,
      password: undefined
    });

    const {dispatch}=useContext(AuthContext);

    const handleChange = e => {
      setCredentials(prev => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleClick = async e => {
      e.preventDefault();
        try {
          const res =await fetch (`${BASE_URl}/auth/register`)
        } catch (err) {
          
        }
    };

    return (
        <section>
            <Container>
                <Row>
                    <Col lg='8' className='m-auto'>
                        <div className='login__container d-flex justify-content-between'>
                            <div className='login__img'>
                                <img src={registerImg} alt="loginImg" />
                            </div>
                            <div className='login__form'>
                                <div className='user'>
                                    <img src={userIcon} alt="userImg" />
                                </div>
                                <h2>Register</h2>
                                <Form onSubmit={handleClick}>
                                  <FormGroup>
                                    <input type='email' placeholder='Username' required id="username" onChange={handleChange}></input>
                                  </FormGroup>
                                  <FormGroup>
                                    <input type='email' placeholder='Email' required id="email" onChange={handleChange}></input>
                                  </FormGroup>
                                  <FormGroup>
                                    <input type='password' placeholder='Password' required id="password" onChange={handleChange}></input>
                                  </FormGroup>
                                  <Button className='btn secondary__btn auth__btn' type="submit" onClick={handleClick}>Regsiter</Button>
                                </Form>
                                <p> Already have an account? <Link to="/login">Login</Link></p>
                            </div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    );
};

export default Register;
